import React, { useEffect, useState } from 'react';
import axios from 'axios';

function WellnessTips({ tips }) {
    return (
      <div className='a-box'>
        <div className='s-b-text'>
          <h3>Wellness Tips</h3>
          <p>Provides practical tips for improving physical and mental health.</p>
          <h4>Example:</h4>
          {tips && tips.map(tip => (
            <div key={tip.id}>
              <h4>{tip.title}</h4>
              <p>{tip.description}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }
  
  function RelaxationTips({ tips }) {
    return (
      <div className='a-box'>
        <div className='s-b-text'>
          <h3>Relaxation Tips</h3>
          <p>It presents relaxation activities to relieve stress and promote tranquility.</p>
          <h4>Example:</h4>
          {tips && tips.map(tip => (
            <div key={tip.id}>
              <h4>{tip.name}</h4>
              <p>{tip.description}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }
  
  function PositiveMessages({ messages }) {
    return (
      <div className='a-box'>
        <div className='s-b-text'>
          <h3>Positive Messages</h3>
          <p>Offers positive messages to inspire and motivate users.</p>
          <h4>Example:</h4>
          {messages && messages.map(message => (
            <div key={message.id}>
              <p>{message.content}</p>
            </div>
          ))}
        </div>
      </div>
    );
}

function Presentation() {
    const [wellnessTips, setWellnessTips] = useState(null);
    const [relaxationTips, setRelaxationTips] = useState(null);
    const [positiveMessages, setPositiveMessages] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/wellness-tips')
      .then(response => {
        setWellnessTips(response.data);
      })
      .catch(error => {
        console.error('Houve um erro ao buscar os dados', error);
      });

    axios.get('http://localhost:5000/api/relaxation-activities')
      .then(response => {
        setRelaxationTips(response.data);
      })
      .catch(error => {
        console.error('Houve um erro ao buscar os dados', error);
      });

    axios.get('http://localhost:5000/api/positive-messages')
      .then(response => {
        setPositiveMessages(response.data);
      })
      .catch(error => {
        console.error('Houve um erro ao buscar os dados', error);
      });
  }, []);

    return (
        <>
        <div id='presentation'>
            <h1>Content</h1>
            <p>Right now, the API is integrated into the website and the messages/tips it offers appear below.</p>
        </div>
        <WellnessTips tips={wellnessTips}/>
        <RelaxationTips tips={relaxationTips}/>
        <PositiveMessages messages={positiveMessages}/> 
    </>
    )
}

export default Presentation;
