import React, { useEffect, useState } from 'react';
import PresentationBox from './PresentationBox';
import axios from 'axios';


function WellnessTips({ tips }) {
    return (
      <div className='card'>
        <h3>Wellness Tips</h3>
        <p>Provides practical tips for improving physical and mental health.</p>
        <h4>Example:</h4>
        {tips && tips.map(tip => (
          <div key={tip.id}>
            <h4>{tip.title}</h4>
            <p>{tip.description}</p>
          </div>
        ))}
      </div>
    );
  }
  
function RelaxationTips({ tips }) {
    return (
      <div className='card'>
        <h3>Relaxation Tips</h3>
        <p>It presents relaxation activities to relieve stress and promote tranquility.</p>
        <h4>Example:</h4>
        {tips && tips.map(tip => (
          <div key={tip.id}>
            <h4>{tip.name}</h4>
            <p>{tip.description}</p>
          </div>
        ))}
      </div>
    );
}

function PositiveMessages({ messages }) {
    return (
      <div className='card'>
        <h3>Positive Messages</h3>
        <p>Offers positive messages to inspire and motivate users.</p>
        <h4>Example:</h4>
        {messages && messages.map(message => (
          <div key={message.id}>
            <p>{message.content}</p>
          </div>
        ))}
      </div>
    );
}

function Presentation() { 
    return (
         <>
            <div id='presentation'>
            <h1>Content</h1>
            <p>Right now, the API is integrated into the website and the messages/tips it offers appear below.</p>
            </div>

            <div id='features'>
            <div className='a-container'>
                <PresentationBox tips={wellnessTips} title='Examples:'/>
                <PresentationBox tips={relaxationTips} title='Examples:'/>
                <PresentationBox messages={positiveMessages} title='Examples:'/>
            </div>
        </div>
          </>
    )
}

export default Presentation;
